file = open('Dump', "w+")
print('Size Are Based Of Kibi, Mibi And Gibibyte (NOT KILO MEGA AND GIGA)\n')
k = int(input("Kb -> "))
m = int(input("Mb -> "))
g = int(input("Gb -> "))

size = k*1024 + m*1024**2 + g*1024**3 / 1000

for i in range(int(size)):
  file.write(''+'\n')
file.close()
print('Done.')

